import React from 'react';
import { useDrag } from 'react-dnd';

const Piece = ({ piece, position, canDrag }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'piece',
    item: { piece, position },
    canDrag: () => canDrag,
    collect: monitor => ({
      isDragging: !!monitor.isDragging(),
    }),
  });

  // Lấy đường dẫn hình ảnh quân cờ
  const getPieceImage = () => {
    const pieceType = piece.type.toLowerCase();
    const colorCode = piece.color === 'white' ? 'w' : 'b';
    
    // Sử dụng cách đặt tên: p_b.png, n_b.png, r_w.png, etc.
    let prefix;
    switch(pieceType) {
      case 'pawn': prefix = 'p'; break;
      case 'knight': prefix = 'n'; break;
      case 'bishop': prefix = 'b'; break;
      case 'rook': prefix = 'r'; break;
      case 'queen': prefix = 'q'; break;
      case 'king': prefix = 'k'; break;
      default: prefix = 'p';
    }
    
    return `/assets/${prefix}_${colorCode}.png`;
  };

  return (
    <div
      ref={canDrag ? drag : null}
      className={`piece ${isDragging ? 'dragging' : ''}`}
      style={{
        backgroundImage: `url(${getPieceImage()})`,
        backgroundSize: 'contain',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
        opacity: isDragging ? 0.5 : 1
      }}
      data-piece-type={piece.type}
      data-piece-color={piece.color}
    />
  );
};

export default Piece;
