import React from 'react';

export default function BoardSquare({ 
  piece, 
  black, 
  position,
  isPossibleMove = false,
  isSelected = false,
  isCheck = false
}) {
  // Xác định các class tùy thuộc vào trạng thái
  const squareClass = [
    black ? 'square-black' : 'square-white',
    isPossibleMove ? 'possible-move' : '',
    isSelected ? 'selected' : '',
    isCheck ? 'in-check' : ''
  ].filter(Boolean).join(' ');

  // Hàm chuyển đổi tên quân cờ sang ký tự viết tắt
  const getPiecePrefix = (pieceType) => {
    // Kiểm tra xem piece.type có phải đã là ký tự đơn chưa
    if (pieceType.length === 1) {
      return pieceType;
    }
    
    switch(pieceType.toLowerCase()) {
      case 'pawn': return 'p';
      case 'knight': return 'n';
      case 'bishop': return 'b';
      case 'rook': return 'r';
      case 'queen': return 'q';
      case 'king': return 'k';
      default: return pieceType; // Giữ nguyên nếu không khớp
    }
  };

  // Cải tiến: Lấy trực tiếp đường dẫn hình ảnh
  const getPiecePath = () => {
    if (!piece || !piece.type) return '';
    const piecePrefix = getPiecePrefix(piece.type);
    const colorSuffix = piece.color === 'white' ? 'w' : 'b';
    return `/assets/${piecePrefix}_${colorSuffix}.png`;
  };

  return (
    <div className={`board-square ${squareClass}`}>
      {piece && (
        <div 
          className="piece-container"
          style={{
            backgroundImage: `url(${getPiecePath()})`,
            backgroundSize: 'contain',
            backgroundPosition: 'center',
            backgroundRepeat: 'no-repeat',
            width: '100%',
            height: '100%'
          }}
        >
          {/* Không sử dụng tag img nữa để tránh lỗi hiển thị khi load thất bại */}
        </div>
      )}
      {isPossibleMove && !piece && <div className="move-indicator"></div>}
    </div>
  );
}
