import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { getToken, setToken, removeToken, getUserId, setUserId, removeUserId } from '../utils/storage';
import { connectSocket, disconnectSocket } from '../services/socket';

// API base URL - match your backend port
const API_URL = "http://localhost:5000/api";

// Create context
const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Check for existing token on initial load
  useEffect(() => {
    const initializeAuth = async () => {
      const token = getToken();
      const userId = getUserId();
      
      if (token && userId) {
        try {
          // Configure axios to use the token
          axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
          
          // Fetch user profile using the stored userId
          const response = await axios.get(`${API_URL}/users/profile/${userId}`);
          
          if (response.data) {
            setUser(response.data);
            setIsAuthenticated(true);
            // Connect socket with authentication
            connectSocket();
          }
        } catch (error) {
          console.error("Error validating token:", error);
          // Invalid or expired token
          logout();
        }
      }
      
      setIsLoading(false);
    };

    initializeAuth();
  }, []);

  // Login function - update to use username instead of email as per your backend
  const login = async (username, password) => {
    try {
      setError(null);
      setIsLoading(true);
      
      const response = await axios.post(`${API_URL}/users/login`, {
        username,
        password
      });
      
      const { token, user: userData } = response.data;
      
      // Save token and user ID to storage
      setToken(token);
      setUserId(userData.id);
      
      // Set authorization header
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      
      // Update state
      setUser(userData);
      setIsAuthenticated(true);
      
      // Connect socket with authentication
      connectSocket();
      
      return userData;
    } catch (error) {
      const message = error.response?.data?.error || "Login failed. Please check your credentials.";
      setError(message);
      throw new Error(message);
    } finally {
      setIsLoading(false);
    }
  };

  // Register function
  const register = async (username, email, password) => {
    try {
      setError(null);
      setIsLoading(true);
      
      const response = await axios.post(`${API_URL}/users/register`, {
        username,
        email,
        password
      });
      
      return response.data;
    } catch (error) {
      const message = error.response?.data?.error || "Registration failed. Please try again.";
      setError(message);
      throw new Error(message);
    } finally {
      setIsLoading(false);
    }
  };

  // Logout function
  const logout = () => {
    // Remove token from storage
    removeToken();
    removeUserId();
    
    // Remove authorization header
    delete axios.defaults.headers.common['Authorization'];
    
    // Update state
    setUser(null);
    setIsAuthenticated(false);
    
    // Disconnect socket
    disconnectSocket();
  };

  // Update user profile - this would need to be implemented in your backend
  const updateProfile = async (userData) => {
    try {
      setIsLoading(true);
      const userId = getUserId();
      const response = await axios.put(`${API_URL}/users/profile/${userId}`, userData);
      
      if (response.data) {
        setUser({...user, ...response.data});
      }
      
      return response.data;
    } catch (error) {
      const message = error.response?.data?.error || "Failed to update profile.";
      setError(message);
      throw new Error(message);
    } finally {
      setIsLoading(false);
    }
  };

  // Context value
  const value = {
    user,
    isAuthenticated,
    isLoading,
    error,
    login,
    logout,
    register,
    updateProfile
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// Custom hook to use the auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  
  return context;
};

export default AuthContext;
