import React, { useState, useEffect, useMemo } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Board from "../components/Board";
import MoveHistory from "../components/MoveHistory";
import GameControls from "../components/GameControls";
import Spinner from "../components/Spinner";
import { socket } from "../services/socket";
import useChess from "../hooks/useChess";
import "../styles/Play.css";

// Safe string helper to avoid substring errors
const safeString = (value) => {
  if (value === null || value === undefined) return '';
  if (typeof value === 'string') return value;
  return String(value);
};

// Safe array helper to avoid map errors
const safeArray = (arr) => {
  if (!arr) return [];
  if (!Array.isArray(arr)) return [];
  return arr;
};

const Play = () => {
  // Hooks for routing and authentication
  const { gameId } = useParams();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  
  // Game state
  const [opponent, setOpponent] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [gameOver, setGameOver] = useState(false);
  const [gameResult, setGameResult] = useState(null);
  const [showResignDialog, setShowResignDialog] = useState(false);
  const [showDrawOfferDialog, setShowDrawOfferDialog] = useState(false);
  const [receivedDrawOffer, setReceivedDrawOffer] = useState(false);
  
  // Debug state
  const [showDebug, setShowDebug] = useState(process.env.NODE_ENV === 'development');
  const [debugInfo, setDebugInfo] = useState({
    gameId: safeString(gameId) || 'Not set',
    socketConnected: socket.connected,
    loadAttempts: 0,
    errors: []
  });

  // Get chess game state from custom hook
  const {
    board,
    currentTurn,
    playerColor,
    moves,
    makeMove,
    position,
    inCheck,
    possibleMoves,
    gameStatus
  } = useChess(gameId);
  
  // Create safe versions of all potentially problematic values
  const safeGameId = useMemo(() => safeString(gameId), [gameId]);
  const safePlayerColor = useMemo(() => safeString(playerColor || 'white'), [playerColor]);
  const safeUsername = useMemo(() => user ? safeString(user.username || 'Player') : 'Player', [user]);
  const safeOpponentName = useMemo(() => opponent ? safeString(opponent.username || 'Opponent') : 'Opponent', [opponent]);
  const safeMoves = useMemo(() => safeArray(moves), [moves]);
  
  // Handle error display safely
  const addError = (message) => {
    try {
      setDebugInfo(prev => ({
        ...prev,
        errors: [
          ...safeArray(prev.errors),
          { time: new Date().toLocaleTimeString(), message: safeString(message) }
        ]
      }));
    } catch (error) {
      console.error("Failed to add error to debug info:", error);
    }
  };
  
  // Add game:matched event handler
  useEffect(() => {
    const handleMatchFound = (data) => {
      try {
        console.log("Match found!", data ? JSON.stringify(data) : 'No data');
        
        if (!data) {
          addError("Empty match data received");
          return;
        }
        
        if (!data.gameId) {
          addError("Invalid match data - missing gameId");
          return;
        }
        
        // Make sure gameId is valid
        const newGameId = safeString(data.gameId);
        if (newGameId === "undefined" || newGameId === "") {
          addError(`Invalid gameId format: ${newGameId}`);
          return;
        }
        
        console.log(`✓ Valid gameId received: ${newGameId}`);
        navigate(`/play/${newGameId}`);
      } catch (error) {
        addError(`Match error: ${error?.message || "Unknown error"}`);
        console.error("Error processing match data:", error);
      }
    };
    
    socket.on("game:matched", handleMatchFound);
    
    return () => {
      socket.off("game:matched", handleMatchFound);
    };
  }, [navigate]);
  
  // Check for undefined gameId in URL
  useEffect(() => {
    try {
      if (window.location.pathname.includes('/play/undefined')) {
        console.error("⚠️ DETECTED 'undefined' IN URL PATH!");
        addError("URL contains 'undefined' instead of a valid gameId");
        
        // Redirect to play page after short delay
        setTimeout(() => navigate('/play'), 3000);
      } else if (safeGameId) {
        console.log(`🎮 Game ID from URL params: ${safeGameId}`);
        setDebugInfo(prev => ({
          ...prev,
          gameId: safeGameId
        }));
      }
    } catch (error) {
      console.error("Error checking URL:", error);
    }
  }, [safeGameId, navigate]);
  
  // Handle authentication redirect
  useEffect(() => {
    try {
      if (!isAuthenticated && !isLoading) {
        navigate("/signin");
      }
    } catch (error) {
      console.error("Error handling auth redirect:", error);
    }
  }, [isAuthenticated, navigate, isLoading]);

  // Track socket connection
  useEffect(() => {
    try {
      const handleConnect = () => {
        console.log("🔌 Socket connected");
        setDebugInfo(prev => ({
          ...prev,
          socketConnected: true
        }));
      };
      
      const handleDisconnect = () => {
        console.log("🔌 Socket disconnected");
        setDebugInfo(prev => ({
          ...prev,
          socketConnected: false
        }));
        addError("Socket disconnected while on game page");
      };
      
      socket.on('connect', handleConnect);
      socket.on('disconnect', handleDisconnect);
      
      return () => {
        socket.off('connect', handleConnect);
        socket.off('disconnect', handleDisconnect);
      };
    } catch (error) {
      console.error("Error setting up socket listeners:", error);
    }
  }, []);

  // Initial game setup
  useEffect(() => {
    try {
      if (!safeGameId || !isAuthenticated) {
        if (!safeGameId) {
          console.log("No gameId provided, showing matchmaking");
        }
        return;
      }

      setIsLoading(true);
      
      // Debug: Log load attempt
      setDebugInfo(prev => ({
        ...prev,
        loadAttempts: (prev.loadAttempts || 0) + 1
      }));
      
      console.log(`🎲 Joining game room for gameId: ${safeGameId}`);

      // Join game room
      socket.emit("game:join", { gameId: safeGameId });

      // Get game data
      const handleGameData = (data) => {
        try {
          console.log(`📊 Received game data:`, data ? JSON.stringify(data) : 'No data');
          
          // SAFETY CHECK: Ensure data is valid before processing
          if (!data) {
            addError("Received empty game data");
            setIsLoading(false);
            return;
          }
          
          // Get opponent information - add safety check here
          let opponentPlayer = null;
          
          try {
            if (data.players && Array.isArray(data.players)) {
              // Safely find opponent
              for (let i = 0; i < data.players.length; i++) {
                const player = data.players[i];
                if (!player) continue; // Skip if player is null/undefined
                
                // Make sure we have user data to compare with
                if (!user || !user.id) {
                  addError("Cannot determine opponent: missing user data");
                  continue;
                }
                
                // Only consider players with IDs
                if (!player.id) continue;
                
                // Found opponent if IDs differ
                if (safeString(player.id) !== safeString(user.id)) {
                  opponentPlayer = {
                    id: safeString(player.id),
                    username: safeString(player.username || 'Opponent'),
                    elo: player.elo || '?'
                  };
                  break;
                }
              }
            }
          } catch (err) {
            console.error("Error finding opponent in players array:", err);
            addError(`Error finding opponent: ${err.message}`);
          }
          
          if (opponentPlayer) {
            setOpponent(opponentPlayer);
            console.log(`👤 Found opponent: ${opponentPlayer.username}`);
          } else {
            console.warn("⚠️ Could not find opponent in game data");
            addError("Could not find opponent in game data");
          }
        } catch (error) {
          console.error("Error processing game data:", error);
          addError(`Game data error: ${error.message}`);
        } finally {
          setIsLoading(false);
        }
      };
      
      const handleGameError = (error) => {
        try {
          console.error(`❌ Game error:`, error);
          const errorMessage = safeString(
            error && typeof error === 'object' ? 
              (error.message || JSON.stringify(error)) : 
              String(error || "Unknown error")
          );
          
          addError(`Game error: ${errorMessage}`);
        } catch (e) {
          console.error("Error handling game error:", e);
        } finally {
          setIsLoading(false);
        }
      };
      
      socket.on("game:data", handleGameData);
      socket.on("game:error", handleGameError);

      // Listen for game over events
      socket.on("game:over", (data) => {
        try {
          console.log(`🏁 Game over:`, data);
          setGameOver(true);
          setGameResult(data && data.result ? data.result : null);
        } catch (error) {
          console.error("Error handling game over:", error);
          addError(`Game over error: ${error.message}`);
        }
      });

      // Listen for draw offers
      socket.on("game:drawOffered", (data) => {
        try {
          console.log(`🤝 Draw offered`);
          setReceivedDrawOffer(true);
        } catch (error) {
          console.error("Error handling draw offer:", error);
          addError(`Draw offer error: ${error.message}`);
        }
      });

      return () => {
        // Clean up socket listeners when component unmounts
        try {
          console.log(`🧹 Cleaning up socket listeners and leaving game`);
          socket.off("game:data", handleGameData);
          socket.off("game:error", handleGameError);
          socket.off("game:over");
          socket.off("game:drawOffered");
          
          if (safeGameId) {
            socket.emit("game:leave", { gameId: safeGameId });
          }
        } catch (error) {
          console.error("Error during cleanup:", error);
        }
      };
    } catch (error) {
      console.error("Error in game setup effect:", error);
      addError(`Game setup error: ${error.message}`);
      setIsLoading(false);
    }
  }, [safeGameId, isAuthenticated, user]);

  // Check for game status changes
  useEffect(() => {
    try {
      if (gameStatus && gameStatus.isGameOver) {
        console.log(`♟️ Game status change to game over:`, gameStatus);
        setGameOver(true);
        setGameResult(gameStatus.result || null);
      }
    } catch (error) {
      console.error("Error handling game status change:", error);
      addError(`Game status error: ${error.message}`);
    }
  }, [gameStatus]);
  
  const handleResign = () => {
    try {
      console.log(`🏳️ Resigning game ${safeGameId}`);
      if (safeGameId) {
        socket.emit("game:resign", { gameId: safeGameId });
      }
      setGameOver(true);
      setGameResult({ 
        type: "resignation", 
        winner: opponent && opponent.id ? opponent.id : null
      });
      setShowResignDialog(false);
    } catch (error) {
      console.error("Error handling resign:", error);
      addError(`Resign error: ${error.message}`);
    }
  };

  const handleOfferDraw = () => {
    try {
      console.log(`🤝 Offering draw in game ${safeGameId}`);
      if (safeGameId) {
        socket.emit("game:offerDraw", { gameId: safeGameId });
      }
      setShowDrawOfferDialog(false);
    } catch (error) {
      console.error("Error offering draw:", error);
      addError(`Draw offer error: ${error.message}`);
    }
  };

  const handleAcceptDraw = () => {
    try {
      console.log(`🤝 Accepting draw in game ${safeGameId}`);
      if (safeGameId) {
        socket.emit("game:acceptDraw", { gameId: safeGameId });
      }
      setGameOver(true);
      setGameResult({ type: "draw", reason: "agreement" });
      setReceivedDrawOffer(false);
    } catch (error) {
      console.error("Error accepting draw:", error);
      addError(`Accept draw error: ${error.message}`);
    }
  };

  const handleDeclineDraw = () => {
    try {
      console.log(`👎 Declining draw in game ${safeGameId}`);
      if (safeGameId) {
        socket.emit("game:declineDraw", { gameId: safeGameId });
      }
      setReceivedDrawOffer(false);
    } catch (error) {
      console.error("Error declining draw:", error);
      addError(`Decline draw error: ${error.message}`);
    }
  };

  const setUndo = () => {
    try {
      console.log(`⏪ Requesting undo in game ${safeGameId}`);
      if (safeGameId) {
        socket.emit("game:requestUndo", { gameId: safeGameId });
      }
    } catch (error) {
      console.error("Error requesting undo:", error);
      addError(`Undo error: ${error.message}`);
    }
  };

  const setRedo = () => {
    try {
      console.log(`⏩ Requesting redo in game ${safeGameId}`);
      if (safeGameId) {
        socket.emit("game:requestRedo", { gameId: safeGameId });
      }
    } catch (error) {
      console.error("Error requesting redo:", error);
      addError(`Redo error: ${error.message}`);
    }
  };

  // Render errors list safely
  const renderErrorsList = () => {
    try {
      const errorsArray = safeArray(debugInfo?.errors);
      if (errorsArray.length === 0) {
        return null;
      }
      
      return (
        <div>
          <h5>Errors:</h5>
          <ul style={{paddingLeft: '15px', margin: '5px 0'}}>
            {errorsArray.map((err, i) => (
              <li key={i}>
                [{safeString(err?.time || 'unknown')}] 
                {safeString(err?.message || 'Error object')}
              </li>
            ))}
          </ul>
        </div>
      );
    } catch (error) {
      console.error("Error rendering errors list:", error);
      return <div>Error displaying errors list</div>;
    }
  };
  
  // Conditional rendering after all hooks
  if (!user && !isLoading) {
    console.error("User object is undefined but authentication check is complete");
    return (
      <div className="error-container">
        <h2>Authentication Error</h2>
        <p>Unable to retrieve your user information. Please try logging in again.</p>
        <button onClick={() => navigate('/signin')}>Go to Sign In</button>
      </div>
    );
  }
  
  if (!safeGameId) {
    return (
      <div className="matchmaking-container">
        <h2>Find a Match</h2>
        
        <div className="matchmaking-options">
          <button onClick={() => {
            try {
              console.log("Looking for casual match");
              socket.emit("game:findMatch", { gameMode: "casual", timeControl: "10min" });
            } catch (error) {
              console.error("Error finding casual match:", error);
              addError(`Find match error: ${error.message}`);
            }
          }}>
            Find Casual Match
          </button>
          
          <button onClick={() => {
            try {
              console.log("Looking for ranked match");
              socket.emit("game:findMatch", { gameMode: "ranked", timeControl: "10min" });
            } catch (error) {
              console.error("Error finding ranked match:", error);
              addError(`Find match error: ${error.message}`);
            }
          }}>
            Find Ranked Match
          </button>
        </div>
        
        {showDebug && (
          <div className="debug-box" style={{
            marginTop: '20px',
            padding: '10px',
            border: '1px solid #666',
            backgroundColor: '#222',
            color: '#eee',
            fontSize: '12px'
          }}>
            <h4>Debug: Listening for match</h4>
            <p>Socket connected: <span style={{ color: socket.connected ? '#4CAF50' : '#F44336' }}>
              {socket.connected ? 'Yes' : 'No'}
            </span></p>
          </div>
        )}
      </div>
    );
  }

  if (isLoading) {
    return (
      <>
        <Spinner />
        {showDebug && (
          <div style={{ position: 'fixed', bottom: '10px', right: '10px', background: '#222', color: '#fff', padding: '10px', maxWidth: '300px', fontSize: '11px' }}>
            <p>Loading game: {safeGameId}</p>
            <p>Attempt #{debugInfo.loadAttempts}</p>
            <p>Socket connected: {debugInfo.socketConnected ? 'Yes' : 'No'}</p>
          </div>
        )}
      </>
    );
  }

  if (!user) {
    return (
      <div className="error-container" style={{ padding: "20px", textAlign: "center" }}>
        <h2>User Data Not Available</h2>
        <p>Unable to load user data. Please try refreshing or sign in again.</p>
        <button 
          onClick={() => navigate('/signin')}
          style={{ padding: "10px 20px", marginTop: "20px" }}
        >
          Go to Sign In
        </button>
      </div>
    );
  }

  return (
    <div className="play-container">
      {/* Debug panel */}
      {showDebug && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '10px',
          zIndex: 1000,
          fontSize: '11px',
          maxWidth: '300px',
          maxHeight: '300px',
          overflow: 'auto'
        }}>
          <button 
            onClick={() => setShowDebug(false)} 
            style={{float: 'right', marginLeft: '5px'}}
          >
            X
          </button>
          <h4>Game Debug</h4>
          <p>Game ID: <code>{safeString(debugInfo?.gameId || 'Not set')}</code></p>
          <p>Socket: <span style={{color: debugInfo?.socketConnected ? 'lime' : 'red'}}>
            {debugInfo?.socketConnected ? 'Connected' : 'Disconnected'}
          </span></p>
          <p>Player: {safeUsername}</p>
          <p>Opponent: {safeOpponentName}</p>
          <p>Color: {safePlayerColor}</p>
          <p>Turn: {safeString(currentTurn || 'Unknown')}</p>
          
          {renderErrorsList()}
        </div>
      )}

      <div className="game-info">
        {opponent && (
          <div className="opponent-info">
            <h3>{safeOpponentName}</h3>
            <div className="rating">Rating: {opponent.elo || '?'}</div>
          </div>
        )}
        
        <div className="game-status">
          {gameOver ? (
            <div className="game-result">
              <h2>Game Over</h2>
              {gameResult && gameResult.type === "checkmate" && (
                <p>
                  Checkmate! {user && gameResult.winner === user.id
                    ? "You won" 
                    : "You lost"}
                </p>
              )}
              {gameResult && gameResult.type === "draw" && (
                <p>Game drawn by {safeString(gameResult.reason) || 'agreement'}</p>
              )}
              {gameResult && gameResult.type === "resignation" && (
                <p>
                  {user && gameResult.winner === user.id
                    ? "Opponent resigned. You won!"
                    : "You resigned. You lost."}
                </p>
              )}
              {gameResult && gameResult.type === "timeout" && (
                <p>
                  {user && gameResult.winner === user.id
                    ? "Opponent ran out of time. You won!"
                    : "You ran out of time. You lost."}
                </p>
              )}
              <button 
                className="new-game-btn"
                onClick={() => navigate("/lobby")}
              >
                Back to Lobby
              </button>
            </div>
          ) : (
            <div>
              <div className="current-turn">
                {currentTurn === safePlayerColor ? "Your turn" : "Opponent's turn"}
              </div>
              {inCheck && <div className="check-alert">Check!</div>}
            </div>
          )}
        </div>
      </div>

      <div className="game-board-container">
        <Board
          board={safeArray(board)}
          position={position && typeof position === 'object' ? position : {}}
          playerColor={safePlayerColor}
          possibleMoves={possibleMoves && typeof possibleMoves === 'object' ? possibleMoves : {}}
          onMove={makeMove}
          disabled={gameOver || currentTurn !== safePlayerColor}
        />
      </div>

      <div className="game-sidebar">
        <div className="move-history-container">
          <MoveHistory moves={safeMoves} />
        </div>
        
        <div className="game-controls-container">
          <GameControls
            onNewGame={() => navigate("/lobby")}
            canResign={!gameOver}
            onResign={() => setShowResignDialog(true)}
            canOfferDraw={!gameOver}
            onOfferDraw={() => setShowDrawOfferDialog(true)}
            canUndo={safeMoves.length > 0 && !gameOver}
            onUndoRequest={() => setUndo()}
            canRedo={true}
            onRedoRequest={() => setRedo()}
          />
        </div>

        {/* Player info */}
        <div className="player-info">
          <h3>You ({safePlayerColor})</h3>
          <div className="rating">Rating: {user && typeof user.elo !== 'undefined' ? user.elo : '?'}</div>
        </div>

        {/* Debug button */}
        {process.env.NODE_ENV === 'development' && !showDebug && (
          <button 
            onClick={() => setShowDebug(true)}
            style={{
              position: 'fixed',
              bottom: '10px',
              right: '10px',
              background: '#444',
              color: '#fff',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            Show Debug
          </button>
        )}
      </div>

      {/* Resign confirmation dialog */}
      {showResignDialog && (
        <div className="dialog-overlay">
          <div className="dialog">
            <h3>Resign Game?</h3>
            <p>Are you sure you want to resign? This will count as a loss.</p>
            <div className="dialog-buttons">
              <button onClick={handleResign} className="confirm-btn">
                Yes, Resign
              </button>
              <button onClick={() => setShowResignDialog(false)} className="cancel-btn">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Draw offer dialog */}
      {showDrawOfferDialog && (
        <div className="dialog-overlay">
          <div className="dialog">
            <h3>Offer Draw?</h3>
            <p>Send a draw offer to your opponent?</p>
            <div className="dialog-buttons">
              <button onClick={handleOfferDraw} className="confirm-btn">
                Yes, Offer Draw
              </button>
              <button onClick={() => setShowDrawOfferDialog(false)} className="cancel-btn">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Received draw offer dialog */}
      {receivedDrawOffer && (
        <div className="dialog-overlay">
          <div className="dialog">
            <h3>Draw Offered</h3>
            <p>Your opponent has offered a draw. Do you accept?</p>
            <div className="dialog-buttons">
              <button onClick={handleAcceptDraw} className="confirm-btn">
                Accept Draw
              </button>
              <button onClick={handleDeclineDraw} className="cancel-btn">
                Decline
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Play;
