import React, { useEffect, useState } from 'react';
import BoardSquare from './BoardSquare';
import '../styles/Board.css';

export default function Board({ board, position, playerColor = 'white', possibleMoves = {}, onMove, disabled = false }) {
  const [currBoard, setCurrBoard] = useState([]);
  const [selectedPiece, setSelectedPiece] = useState(null);

  // Determine if board should be flipped (player is black)
  const isFlipped = playerColor === 'black';

  useEffect(() => {
    console.log("Board updated:", board);
    setCurrBoard(board.flat());
  }, [board]);

  // Add this effect to log when playerColor changes
  useEffect(() => {
    console.log("Player color changed to:", playerColor);
    console.log("Board is flipped:", isFlipped);
  }, [playerColor, isFlipped]);

  function getXYPosition(i) {
    let x, y;

    if (isFlipped) {
      // Flipped coordinates for black player
      x = 7 - (i % 8);
      y = Math.floor(i / 8);
    } else {
      // Normal coordinates for white player
      x = i % 8;
      y = Math.abs(Math.floor(i / 8) - 7);
    }

    return { x, y };
  }

  function isBlack(i) {
    const { x, y } = getXYPosition(i);
    return (x + y) % 2 === 1;
  }

  function getPosition(i) {
    const { x, y } = getXYPosition(i);
    const letter = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'][x];
    return `${letter}${y + 1}`;
  }

  // Convert from position notation (e.g. "e4") to board index
  // eslint-disable-next-line no-unused-vars
  function getIndexFromPosition(pos) {
    const x = pos.charCodeAt(0) - 97; // 'a' is 97 in ASCII
    const y = parseInt(pos[1]) - 1;

    if (isFlipped) {
      // Calculate index for flipped board
      return y * 8 + (7 - x);
    } else {
      // Calculate index for normal board
      return (7 - y) * 8 + x;
    }
  }

  // Check if a position is a valid move
  // eslint-disable-next-line no-unused-vars
  function isPossibleMove(position) {
    if (!selectedPiece) return false;

    // Debug the possibleMoves structure
    console.log("Checking move possibility:", {
      from: selectedPiece,
      to: position,
      possibleMovesForPiece: possibleMoves[selectedPiece]
    });

    return possibleMoves[selectedPiece]?.includes(position);
  }

  // Completely rewrite the handleSquareClick function to fix black piece movement
  function handleSquareClick(position, piece) {
    if (disabled) {
      console.log("Board is disabled, cannot move");
      return;
    }

    console.log("Clicked on square:", position);
    console.log("Piece at position:", piece);
    console.log("Player color:", playerColor);
    console.log("Selected piece:", selectedPiece);

    // If we've already selected a piece
    if (selectedPiece) {
      // Try to move to a new position
      if (position !== selectedPiece) {
        // Use isPossibleMove to check if this is a valid move
        if (isPossibleMove(position)) {
          console.log(`Valid move: ${selectedPiece} → ${position}`);
          onMove(selectedPiece, position);
        } else {
          console.log("Invalid move - not in possible moves list");
        }
      }
      // Always clear the selection after attempting a move
      setSelectedPiece(null);
      setSelectedPiece(null);
    } 
    // No piece selected yet - select one if it's the player's color
    else if (piece && piece.color === playerColor) {
      console.log("Selecting piece at", position);
      setSelectedPiece(position);

      // Debug possible moves for this piece
      if (possibleMoves[position]) {
        console.log("Possible moves for this piece:", possibleMoves[position]);
      } else {
        console.log("No possible moves found for this piece");
      }
    }
  }

  // Check if king is in check
  function isCheck(i) {
    const piece = currBoard[i];
    return position?.inCheck && piece && piece.type === 'king' && piece.color === position.inCheck;
  }

  // First, update the way board is rendered and positions are calculated
  function renderSquares() {
    const squares = [];

    // This is critical for debugging - add this code
    console.log("Rendering board with:", {
      playerColor,
      isFlipped,
      selectedPiece,
      possibleMovesCount: Object.keys(possibleMoves).length
    });

    for (let i = 0; i < 64; i++) {
      // Calculate the actual index based on whether board is flipped
      const actualIndex = isFlipped ? (63 - i) : i;
      const piece = currBoard[actualIndex];
      const pos = getPosition(actualIndex);

      // Add extensive debugging for black pieces
      if (piece && piece.color === 'black' && playerColor === 'black') {
        const hasMoves = possibleMoves[pos] && possibleMoves[pos].length > 0;
        console.log(`Black piece at ${pos}: type=${piece.type}, hasMoves=${hasMoves}`);
        if (hasMoves) {
          console.log(`Available moves for ${pos}:`, possibleMoves[pos]);
        }
      }

      // Add a simple click handler that will work regardless of visual flipping
      squares.push(
        <div 
          key={i} 
          className={`square ${isBlack(actualIndex) ? 'dark' : 'light'}`}
          onClick={() => handleSquareClick(pos, piece)}
        >
          <BoardSquare
            piece={piece}
            black={isBlack(actualIndex)}
            position={pos}
            isPossibleMove={selectedPiece && possibleMoves[selectedPiece]?.includes(pos)}
            isSelected={selectedPiece === pos}
            isCheck={isCheck(actualIndex)}
          />
        </div>
      );
    }

    return squares;
  }

  return (
    <div className="chess-board">
      <div className="board">
        {renderSquares()}
      </div>

      {/* Coordinates that flip with the board */}
      <div className="board-coordinates files">
        {(isFlipped ? ['h', 'g', 'f', 'e', 'd', 'c', 'b', 'a'] : ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']).map(file => (
          <div key={file}>{file}</div>
        ))}
      </div>
      <div className="board-coordinates ranks">
        {(isFlipped ? [1, 2, 3, 4, 5, 6, 7, 8] : [8, 7, 6, 5, 4, 3, 2, 1]).map(rank => (
          <div key={rank}>{rank}</div>
        ))}
      </div>
    </div>
  );
}
