import React from 'react';

export default function BoardSquare({ 
  piece, 
  black, 
  position,
  isPossibleMove = false,
  isSelected = false,
  isCheck = false
}) {
  // Xác định các class tùy thuộc vào trạng thái
  const squareClass = [
    black ? 'square-black' : 'square-white',
    isPossibleMove ? 'possible-move' : '',
    isSelected ? 'selected' : '',
    isCheck ? 'in-check' : ''
  ].filter(Boolean).join(' ');

  // Thêm kiểm tra an toàn
  const getPieceImage = () => {
    if (!piece || !piece.type) return null;
    // Sử dụng trực tiếp type vì nó đã là ký tự đơn
    return `/assets/${piece.type}_${piece.color === 'white' ? 'w' : 'b'}.png`;
  };

  return (
    <div className={`board-square ${squareClass}`}>
      {piece && (
        <div className="piece-container">
          <img 
            src={getPieceImage()}
            alt={`${piece.color} ${piece.type}`} 
            className="piece" 
            onError={(e) => {
              console.error(`Lỗi tải hình ảnh: ${piece.type}_${piece.color}`);
              e.target.style.display = 'none';
            }}
          />
        </div>
      )}
      {isPossibleMove && !piece && <div className="move-indicator"></div>}
    </div>
  );
}
