import { io } from 'socket.io-client';
import { getToken } from '../utils/storage';

// Use the same port as your web server - change this if necessary
// This should match wherever your backend socket.io server is running
const SOCKET_URL = process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000';

// Create socket instance with better configuration
export const socket = io(SOCKET_URL, {
  autoConnect: false,
  reconnection: true,
  reconnectionAttempts: 5,
  reconnectionDelay: 1000,
  auth: {
    token: getToken()  // Use the function result, not the function itself
  }
});

// Connect socket with current token
export const connectSocket = () => {
  if (getToken()) {
    socket.auth = { token: getToken() };
    socket.connect();
    return true;
  }
  return false;
};

// Disconnect socket
export const disconnectSocket = () => {
  socket.disconnect();
};

// Socket event names - centralizing for consistency
export const SOCKET_EVENTS = {
  // Connection events
  CONNECT: 'connect',
  CONNECT_ERROR: 'connect_error',
  DISCONNECT: 'disconnect',
  
  // Game events
  GAME_MATCHED: 'game:matched',
  GAME_SEARCHING: 'game:searching',
  GAME_ERROR: 'game:error',
  GAME_UPDATE: 'game:update',
  GAME_OVER: 'game:over',
  GAME_SEARCH_CANCELED: 'game:searchCanceled',
  
  // Matchmaking events
  MATCHMAKING_UPDATE: 'matchmaking:update',
  
  // Lobby events
  LOBBY_ACTIVE_PLAYERS: 'lobby:activePlayers'
};

// Debug all incoming socket events
if (process.env.NODE_ENV === 'development') {
  const originalOn = socket.on;
  
  socket.on = function(eventName, callback) {
    const wrappedCallback = (data) => {
      console.log(`ðŸ”½ Socket received '${eventName}'`, data);
      return callback(data);
    };
    return originalOn.call(socket, eventName, wrappedCallback);
  };
  
  // Also debug socket emissions
  const originalEmit = socket.emit;
  socket.emit = function(eventName, ...args) {
    console.log(`ðŸ”¼ Socket emitting '${eventName}'`, args);
    return originalEmit.apply(socket, [eventName, ...args]);
  };
}

// Listen for connection/errors
socket.on(SOCKET_EVENTS.CONNECT, () => {
  console.log('âœ… Socket connected successfully');
});

socket.on(SOCKET_EVENTS.CONNECT_ERROR, (error) => {
  console.error('âŒ Socket connection error:', error.message);
  
  // If authentication error, handle it
  if (error.message === 'authentication_error') {
    console.error('Authentication failed for socket connection');
    // You could dispatch an action or call a callback here
  }
});

socket.on(SOCKET_EVENTS.DISCONNECT, (reason) => {
  console.log(`Socket disconnected: ${reason}`);
});

// Export default socket instance
export default socket;
