import React, { useState, useEffect } from 'react';

const Toast = ({ message, type = 'info', duration = 5000 }) => {
  const [visible, setVisible] = useState(true);
  
  useEffect(() => {
    const timer = setTimeout(() => {
      setVisible(false);
    }, duration);
    
    return () => {
      clearTimeout(timer);
    };
  }, [duration]);
  
  if (!visible) return null;
  
  const getTypeStyles = () => {
    switch(type) {
      case 'success':
        return { backgroundColor: '#4CAF50', color: 'white' };
      case 'error':
        return { backgroundColor: '#F44336', color: 'white' };
      case 'warning':
        return { backgroundColor: '#FF9800', color: 'black' };
      case 'info':
      default:
        return { backgroundColor: '#2196F3', color: 'white' };
    }
  };
  
  return (
    <div style={{
      position: 'fixed',
      top: '20px',
      left: '50%',
      transform: 'translateX(-50%)',
      padding: '10px 20px',
      borderRadius: '4px',
      zIndex: 9999,
      boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
      ...getTypeStyles()
    }}>
      {message}
      <button 
        onClick={() => setVisible(false)}
        style={{
          marginLeft: '10px',
          background: 'none',
          border: 'none',
          color: 'inherit',
          cursor: 'pointer',
          fontWeight: 'bold'
        }}
      >
        Ã—
      </button>
    </div>
  );
};

export default Toast;
