import React, { useEffect, useState } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './context/AuthContext';
import Home from './pages/Home';
import Lobby from './pages/Lobby';
import Play from './pages/Play';
import SignIn from './pages/SignIn';
import SignUp from './pages/SignUp';
import PWRec from './pages/PWRec';
import ResetPassword from './pages/ResetPW';
import Spinner from './components/Spinner';
import { connectSocket, disconnectSocket, socket, SOCKET_EVENTS } from './services/socket';
import Toast from './components/Toast'; // You'll need to create this component
import './styles/App.css';

// Protected route component
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated, isLoading } = useAuth();
  
  if (isLoading) {
    return <Spinner />;
  }
  
  if (!isAuthenticated) {
    return <Navigate to="/signin" replace />;
  }
  
  return children;
};

const App = () => {
  const { isAuthenticated } = useAuth();
  const [socketConnected, setSocketConnected] = useState(false);
  const [socketError, setSocketError] = useState(null);
  const [showDebug, setShowDebug] = useState(process.env.NODE_ENV === 'development');
  const [debugEvents, setDebugEvents] = useState([]);
  
  // Connect to socket.io when authenticated
  useEffect(() => {
    if (isAuthenticated) {
      const connected = connectSocket();
      console.log('Socket connection attempt result:', connected);
      
      // Add socket state listeners
      const handleConnect = () => {
        console.log('Socket connected successfully!');
        setSocketConnected(true);
        setSocketError(null);
      };
      
      const handleError = (error) => {
        console.error('Socket connection error:', error);
        setSocketConnected(false);
        setSocketError(`Connection error: ${error.message || 'Unknown error'}`);
      };
      
      const handleDisconnect = (reason) => {
        console.log('Socket disconnected:', reason);
        setSocketConnected(false);
        if (reason === 'io server disconnect') {
          // The server forcefully disconnected the socket
          setSocketError('Server disconnected the socket. You may need to log in again.');
        }
      };
      
      // Debug - capture all events for development
      if (process.env.NODE_ENV === 'development') {
        const originalOn = socket.on;
        socket.on = function(eventName, callback) {
          const wrappedCallback = (data) => {
            const eventData = {
              event: eventName,
              data,
              time: new Date().toLocaleTimeString()
            };
            
            console.log(`👂 Socket event '${eventName}' received:`, data);
            
            // Keep last 10 events
            setDebugEvents(prev => [eventData, ...prev].slice(0, 10));
            
            // Special handling for game:matched to diagnose undefined gameId
            if (eventName === 'game:matched') {
              console.log(`🎮 MATCH FOUND - gameId: ${data?.gameId || 'UNDEFINED'}`);
              if (!data || !data.gameId) {
                console.error('❌ ERROR: Missing gameId in game:matched event!', data);
              }
            }
            
            return callback(data);
          };
          
          return originalOn.call(socket, eventName, wrappedCallback);
        };
      }
      
      socket.on(SOCKET_EVENTS.CONNECT, handleConnect);
      socket.on(SOCKET_EVENTS.CONNECT_ERROR, handleError);
      socket.on(SOCKET_EVENTS.DISCONNECT, handleDisconnect);
      
      return () => {
        socket.off(SOCKET_EVENTS.CONNECT, handleConnect);
        socket.off(SOCKET_EVENTS.CONNECT_ERROR, handleError);
        socket.off(SOCKET_EVENTS.DISCONNECT, handleDisconnect);
        disconnectSocket();
      };
    } else {
      disconnectSocket();
    }
  }, [isAuthenticated]);
  
  return (
    <div className="app">
      {/* Socket connection status for debugging */}
      {showDebug && (
        <div className="socket-debug-panel" style={{
          position: 'fixed',
          bottom: 0,
          right: 0,
          backgroundColor: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '10px',
          zIndex: 9999,
          fontSize: '12px',
          maxWidth: '400px'
        }}>
          <div>
            <strong>Socket Status:</strong> 
            <span style={{ color: socketConnected ? '#4CAF50' : '#F44336' }}>
              {socketConnected ? 'Connected' : 'Disconnected'}
            </span>
            {socketError && (
              <div style={{ color: '#F44336' }}>{socketError}</div>
            )}
          </div>
          <div>
            <strong>Recent Events:</strong>
            <button 
              onClick={() => setShowDebug(false)} 
              style={{float: 'right', marginLeft: '10px'}}
            >
              Hide
            </button>
            <ul style={{
              maxHeight: '200px',
              overflowY: 'auto',
              margin: '5px 0',
              padding: '0 0 0 20px'
            }}>
              {debugEvents.map((evt, i) => (
                <li key={i} style={{
                  marginBottom: '5px',
                  fontSize: '11px',
                  wordBreak: 'break-all'
                }}>
                  <strong>[{evt.time}] {evt.event}</strong>: 
                  {evt.event === 'game:matched' 
                    ? ` gameId: ${JSON.stringify(evt.data?.gameId)}` 
                    : ' ' + JSON.stringify(evt.data).substring(0, 100) + (JSON.stringify(evt.data).length > 100 ? '...' : '')}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
      
      {/* Show a toast message when not connected but authenticated */}
      {isAuthenticated && !socketConnected && (
        <Toast 
          message="Connection to game server lost. Reconnecting..." 
          type="warning"
        />
      )}
      
      <Routes>
        {/* Public routes */}
        <Route path="/" element={<Home />} />
        <Route path="/signin" element={<SignIn />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/pwrecovery" element={<PWRec />} />
        
        {/* Protected routes */}
        <Route 
          path="/lobby" 
          element={
            <ProtectedRoute>
              <Lobby />
            </ProtectedRoute>
          } 
        />
        
        <Route 
          path="/play" 
          element={
            <ProtectedRoute>
              <Play />
            </ProtectedRoute>
          } 
        />
        
        <Route 
          path="/play/:gameId" 
          element={
            <ProtectedRoute>
              <Play />
            </ProtectedRoute>
          } 
        />

        <Route 
          path="/reset-password/:token" 
          element={
            <ResetPassword />
          }
        />

        {/* Catch-all route */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
};

export default App;
