import React, { useEffect, useState } from 'react';
import BoardSquare from './BoardSquare';
import '../styles/Board.css';

export default function Board({ board, position, playerColor = 'white', possibleMoves = {}, onMove, disabled = false }) {
  const [currBoard, setCurrBoard] = useState([]);
  const [selectedPiece, setSelectedPiece] = useState(null);

  // Determine if board should be flipped (player is black)
  const isFlipped = playerColor === 'black';

  useEffect(() => {
    console.log("Board updated:", board);
    setCurrBoard(board.flat());
  }, [board]);

  function getXYPosition(i) {
    let x, y;

    if (isFlipped) {
      // Flipped coordinates for black player
      x = 7 - (i % 8);
      y = Math.floor(i / 8);
    } else {
      // Normal coordinates for white player
      x = i % 8;
      y = Math.abs(Math.floor(i / 8) - 7);
    }

    return { x, y };
  }

  function isBlack(i) {
    const { x, y } = getXYPosition(i);
    return (x + y) % 2 === 1;
  }

  function getPosition(i) {
    const { x, y } = getXYPosition(i);
    const letter = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'][x];
    return `${letter}${y + 1}`;
  }

  // Convert from position notation (e.g. "e4") to board index
  // eslint-disable-next-line no-unused-vars
  function getIndexFromPosition(pos) {
    const x = pos.charCodeAt(0) - 97; // 'a' is 97 in ASCII
    const y = parseInt(pos[1]) - 1;

    if (isFlipped) {
      // Calculate index for flipped board
      return y * 8 + (7 - x);
    } else {
      // Calculate index for normal board
      return (7 - y) * 8 + x;
    }
  }

  // Check if a position is a valid move
  function isPossibleMove(position) {
    if (!selectedPiece) return false;
    return possibleMoves[selectedPiece]?.includes(position);
  }

  // Handle square click
  function handleSquareClick(position, piece) {
    if (disabled) return;

    if (selectedPiece) {
      if (isPossibleMove(position)) {
        // No need to flip coordinates here - the position is already in standard chess notation
        onMove(selectedPiece, position);
      }
      setSelectedPiece(null);
    } 
    else if (piece && piece.color === playerColor) {
      setSelectedPiece(position);
    }
  }

  // Check if king is in check
  function isCheck(i) {
    const piece = currBoard[i];
    return position?.inCheck && piece && piece.type === 'king' && piece.color === position.inCheck;
  }

  // Generate board squares in correct order based on player color
  function renderSquares() {
    const squares = [];

    for (let i = 0; i < 64; i++) {
      // Calculate the actual index based on whether board is flipped
      const actualIndex = isFlipped ? (63 - i) : i;
      const piece = currBoard[actualIndex];
      const pos = getPosition(actualIndex);

      // Add this debug log to check position calculations
      if (piece && piece.color === playerColor) {
        console.log(`Square ${pos}: piece=${piece.type}, color=${piece.color}, canMove=${possibleMoves[pos]?.length > 0}`);
      }

      squares.push(
        <div 
          key={i} 
          className={`square ${isBlack(actualIndex) ? 'dark' : 'light'}`}
          onClick={() => handleSquareClick(pos, piece)}
        >
          <BoardSquare
            piece={piece}
            black={isBlack(actualIndex)}
            position={pos}
            isPossibleMove={isPossibleMove(pos)}
            isSelected={selectedPiece === pos}
            isCheck={isCheck(actualIndex)}
          />
        </div>
      );
    }

    return squares;
  }

  return (
    <div className="chess-board">
      <div className="board">
        {renderSquares()}
      </div>

      {/* Coordinates that flip with the board */}
      <div className="board-coordinates files">
        {(isFlipped ? ['h', 'g', 'f', 'e', 'd', 'c', 'b', 'a'] : ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h']).map(file => (
          <div key={file}>{file}</div>
        ))}
      </div>
      <div className="board-coordinates ranks">
        {(isFlipped ? [1, 2, 3, 4, 5, 6, 7, 8] : [8, 7, 6, 5, 4, 3, 2, 1]).map(rank => (
          <div key={rank}>{rank}</div>
        ))}
      </div>
    </div>
  );
}
