import React from 'react';

// Import các hình ảnh quân cờ
import p_w from './assets/p_w.png';
import n_w from './assets/n_w.png';
import b_w from './assets/b_w.png';
import r_w from './assets/r_w.png';
import q_w from './assets/q_w.png';
import k_w from './assets/k_w.png';
import p_b from './assets/p_b.png';
import n_b from './assets/n_b.png';
import b_b from './assets/b_b.png';
import r_b from './assets/r_b.png';
import q_b from './assets/q_b.png';
import k_b from './assets/k_b.png';

export default function BoardSquare({ 
  piece, 
  black, 
  position,
  isPossibleMove = false,
  isSelected = false,
  isCheck = false
}) {
  // Xác định các class tùy thuộc vào trạng thái
  const squareClass = [
    black ? 'square-black' : 'square-white',
    isPossibleMove ? 'possible-move' : '',
    isSelected ? 'selected' : '',
    isCheck ? 'in-check' : ''
  ].filter(Boolean).join(' ');

  // Tạo object ánh xạ loại quân cờ và màu sắc tới hình ảnh 
  const pieceImages = {
    'pawn': {
      'white': p_w,
      'black': p_b
    },
    'knight': {
      'white': n_w,
      'black': n_b
    },
    'bishop': {
      'white': b_w,
      'black': b_b
    },
    'rook': {
      'white': r_w,
      'black': r_b
    },
    'queen': {
      'white': q_w,
      'black': q_b
    },
    'king': {
      'white': k_w,
      'black': k_b
    }
  };

  // Hàm lấy hình ảnh dựa trên loại quân và màu sắc
  const getPieceImage = () => {
    if (!piece) return null;
    const type = piece.type.toLowerCase();
    const color = piece.color;
    return pieceImages[type][color];
  };

  return (
    <div className={`board-square ${squareClass}`}>
      {piece && (
        <div className="piece-container">
          <img 
            src={getPieceImage()}
            alt={`${piece.color} ${piece.type}`} 
            className="piece" 
          />
        </div>
      )}
      {isPossibleMove && !piece && <div className="move-indicator"></div>}
    </div>
  );
}
