import React from 'react';
import '../styles/MoveHistory.css';

const MoveHistory = ({ moves = [] }) => {
  // Format move for display (e.g., "e2-e4", "Nf3", "O-O")
  const formatMove = (move) => {
    if (!move) return '';
    
    // Special case for castling
    if (move.castling) {
      return move.castling === 'kingside' ? 'O-O' : 'O-O-O';
    }
    
    // For captures
    if (move.capture) {
      const pieceSymbol = getPieceSymbol(move.piece);
      return `${pieceSymbol}${move.from.slice(0, 1)}x${move.to}`;
    }
    
    // For regular moves
    const pieceSymbol = getPieceSymbol(move.piece);
    return `${pieceSymbol}${move.from}-${move.to}`;
  };
  
  // Get the symbol for a piece
  const getPieceSymbol = (piece) => {
    if (!piece || piece === 'pawn') return '';
    const symbols = {
      'knight': 'N',
      'bishop': 'B',
      'rook': 'R',
      'queen': 'Q',
      'king': 'K'
    };
    return symbols[piece] || '';
  };

  // Group moves by pairs using the color property or first character of from position
  const groupMovesInPairs = (moves) => {
    const pairs = [];
    let currentPair = { white: null, black: null };
    
    moves.forEach(move => {
      // Check if the move has a color property, otherwise determine color from the game state
      // This assumes your move objects include a color property
      const isWhite = move.color === 'white';
      
      if (isWhite) {
        // If we already have a white move in the current pair, create a new pair
        if (currentPair.white) {
          pairs.push(currentPair);
          currentPair = { white: move, black: null };
        } else {
          currentPair.white = move;
        }
      } else {
        // It's a black move
        currentPair.black = move;
        // After adding a black move, push the pair to the array
        pairs.push(currentPair);
        currentPair = { white: null, black: null };
      }
    });
    
    // Don't forget to add the last pair if it has any moves
    if (currentPair.white || currentPair.black) {
      pairs.push(currentPair);
    }
    
    return pairs;
  };

  const movePairs = groupMovesInPairs(moves);

  return (
    <div className="move-history">
      <h3>Move History</h3>
      <div className="move-list">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>White</th>
              <th>Black</th>
            </tr>
          </thead>
          <tbody>
            {movePairs.map((pair, index) => (
              <tr key={index}>
                <td className="move-number">{index + 1}.</td>
                <td className="move white-move">{formatMove(pair.white)}</td>
                <td className="move black-move">{formatMove(pair.black)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MoveHistory;
