import React from 'react';

export default function BoardSquare({ 
  piece, 
  black, 
  position,
  isPossibleMove = false,
  isSelected = false,
  isCheck = false
}) {
  // Xác định các class tùy thuộc vào trạng thái
  const squareClass = [
    black ? 'square-black' : 'square-white',
    isPossibleMove ? 'possible-move' : '',
    isSelected ? 'selected' : '',
    isCheck ? 'in-check' : ''
  ].filter(Boolean).join(' ');

  // Hàm chuyển đổi tên quân cờ sang ký tự viết tắt
  const getPiecePrefix = (pieceType) => {
    // Kiểm tra xem piece.type có phải đã là ký tự đơn chưa
    if (pieceType.length === 1) {
      return pieceType;
    }
    
    switch(pieceType.toLowerCase()) {
      case 'pawn': return 'p';
      case 'knight': return 'n';
      case 'bishop': return 'b';
      case 'rook': return 'r';
      case 'queen': return 'q';
      case 'king': return 'k';
      default: return pieceType; // Giữ nguyên nếu không khớp
    }
  };

  // Thêm kiểm tra an toàn
  const getPieceImage = () => {
    if (!piece || !piece.type) return null;
    
    // Sử dụng hàm chuyển đổi để lấy ký tự đại diện cho quân cờ
    const piecePrefix = getPiecePrefix(piece.type);
    return `/assets/${piecePrefix}_${piece.color === 'white' ? 'w' : 'b'}.png`;
  };

  return (
    <div className={`board-square ${squareClass}`}>
      {piece && (
        <div className="piece-container">
          <img 
            src={getPieceImage()}
            alt={`${piece.color} ${piece.type}`} 
            className="piece" 
            onError={(e) => {
              // Ghi log lỗi nhưng không hiển thị console.error trong UI
              console.log(`Không thể tải hình ảnh: ${getPiecePrefix(piece.type)}_${piece.color === 'white' ? 'w' : 'b'}`);
              // Thử tải hình ảnh dự phòng hoặc ẩn đi
              e.target.style.display = 'none';
            }}
          />
        </div>
      )}
      {isPossibleMove && !piece && <div className="move-indicator"></div>}
    </div>
  );
}
