import React from 'react';

export default function BoardSquare({ 
  piece, 
  black, 
  position,
  isPossibleMove = false,
  isSelected = false,
  isCheck = false
}) {
  // Xác định các class tùy thuộc vào trạng thái
  const squareClass = [
    black ? 'square-black' : 'square-white',
    isPossibleMove ? 'possible-move' : '',
    isSelected ? 'selected' : '',
    isCheck ? 'in-check' : ''
  ].filter(Boolean).join(' ');

  // Ánh xạ từ loại quân cờ sang ký tự viết tắt trong tên file
  const getPiecePrefix = (pieceType) => {
    switch(pieceType?.toLowerCase()) {
      case 'pawn': return 'p';
      case 'knight': return 'n';
      case 'bishop': return 'b';
      case 'rook': return 'r';
      case 'queen': return 'q';
      case 'king': return 'k';
      default: return 'p';
    }
  };

  // Thêm kiểm tra an toàn
  const getPieceImage = () => {
    if (!piece || !piece.type) return null;
    return `/assets/${getPiecePrefix(piece.type)}_${piece.color === 'white' ? 'w' : 'b'}.png`;
  };

  return (
    <div className={`board-square ${squareClass}`}>
      {piece && (
        <div className="piece-container">
          <img 
            src={getPieceImage()}
            alt={`${piece.color} ${piece.type}`} 
            className="piece" 
          />
        </div>
      )}
      {isPossibleMove && !piece && <div className="move-indicator"></div>}
    </div>
  );
}
