import React from 'react';

// Import hình ảnh quân cờ từ thư mục assets
// Quân đen
import p_b from '../components/assets/p_b.png';
import r_b from '../components/assets/r_b.png';
import n_b from '../components/assets/n_b.png';
import b_b from '../components/assets/b_b.png';
import q_b from '../components/assets/q_b.png';
import k_b from '../components/assets/k_b.png';
// Quân trắng
import p_w from '../components/assets/p_w.png';
import r_w from '../components/assets/r_w.png';
import n_w from '../components/assets/n_w.png';
import b_w from '../components/assets/b_w.png';
import q_w from '../components/assets/q_w.png';
import k_w from '../components/assets/k_w.png';

export default function BoardSquare({ 
  piece, 
  black, 
  position,
  isPossibleMove = false,
  isSelected = false,
  isCheck = false
}) {
  // Xác định các class tùy thuộc vào trạng thái
  const squareClass = [
    black ? 'square-black' : 'square-white',
    isPossibleMove ? 'possible-move' : '',
    isSelected ? 'selected' : '',
    isCheck ? 'in-check' : ''
  ].filter(Boolean).join(' ');

  // Lấy hình ảnh quân cờ tương ứng
  const getPieceImage = () => {
    if (!piece) return null;
    
    const pieceImages = {
      'pawn': {
        'white': p_w,
        'black': p_b
      },
      'rook': {
        'white': r_w,
        'black': r_b
      },
      'knight': {
        'white': n_w,
        'black': n_b
      },
      'bishop': {
        'white': b_w,
        'black': b_b
      },
      'queen': {
        'white': q_w,
        'black': q_b
      },
      'king': {
        'white': k_w,
        'black': k_b
      }
    };
    
    return pieceImages[piece.type.toLowerCase()]?.[piece.color];
  };

  return (
    <div className={`board-square ${squareClass}`}>
      {piece && (
        <div className="piece-container">
          <img 
            src={getPieceImage()}
            alt={`${piece.color} ${piece.type}`} 
            className="piece" 
          />
        </div>
      )}
      {isPossibleMove && !piece && <div className="move-indicator"></div>}
    </div>
  );
}
