import React, { useEffect, useState } from 'react';
import Square from './Square';
import Piece from './Piece';
import { useDrop } from 'react-dnd';
import { handleMove, gameSubject } from './Game';
import Promote from './Promote';

export function BoardSquare({ piece, black, position }) {
  const [promotion, setPromotion] = useState(null);

  const [, drop] = useDrop({
    accept: 'piece',
    drop: (item) => {
      const [fromPosition] = item.id.split('_');
      console.log(`Dropped piece from ${fromPosition} to ${position}`);
      handleMove(fromPosition, position);
    },
  });

  useEffect(() => {
    const subscription = gameSubject.subscribe(({ pendingPromotion }) =>
      pendingPromotion && pendingPromotion.to === position
        ? setPromotion(pendingPromotion)
        : setPromotion(null)
    );
    return () => subscription.unsubscribe();
  }, [position]);

  return (
    <div className="board-square" ref={drop}>
      <Square black={black}>
        {promotion ? (
          <Promote promotion={promotion} />
        ) : piece ? (
          <Piece piece={piece} position={position} />
        ) : null}
      </Square>
    </div>
  );
}

// Removed duplicate React import

const BoardCoordinates = ({ type, playerColor }) => {
  const isFlipped = playerColor === 'black';
  
  if (type === 'files') {
    const files = isFlipped ? 
      ['h', 'g', 'f', 'e', 'd', 'c', 'b', 'a'] : 
      ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h'];
    
    return (
      <div className="board-coordinates files">
        {files.map(file => <div key={file}>{file}</div>)}
      </div>
    );
  } else {
    const ranks = isFlipped ? 
      [1, 2, 3, 4, 5, 6, 7, 8] : 
      [8, 7, 6, 5, 4, 3, 2, 1];
    
    return (
      <div className="board-coordinates ranks">
        {ranks.map(rank => <div key={rank}>{rank}</div>)}
      </div>
    );
  }
};

export default BoardCoordinates;

