import { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import { socket } from '../services/socket';

/**
 * Custom hook to manage chess game state and socket communication
 * @param {string} gameId - The ID of the current game
 * @returns {Object} Chess game state and methods
 */
const useChess = (gameId) => {
  const { user } = useAuth();

  // Game state
  const [board, setBoard] = useState(initialBoard());
  const [position, setPosition] = useState(null);
  const [moves, setMoves] = useState([]);
  const [currentTurn, setCurrentTurn] = useState('white');
  const [playerColor, setPlayerColor] = useState(null);
  const [possibleMoves, setPossibleMoves] = useState({});
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [inCheck, setInCheck] = useState(false);
  const [gameStatus, setGameStatus] = useState({
    isGameOver: false,
    result: null
  });
  const [moveHistory, setMoveHistory] = useState([]);
  const [undoStack, setUndoStack] = useState([]);

  // Initialize game when component mounts
  useEffect(() => {
    if (!gameId) return;

    // Join the game
    socket.emit('game:join', { gameId });

    // Handle initial game data
    const handleGameData = (data) => {
      if (!data) return;

      setBoard(data.board || initialBoard());
      setPosition(data.position || null);
      setMoves(data.moves || []);
      setCurrentTurn(data.currentTurn || 'white');

      // Set player color based on the player's user ID
      if (data.players && Array.isArray(data.players) && user) {
        const playerData = data.players.find(p => p && p.id === user.id);
        setPlayerColor(playerData?.color || 'white');
      }

      // Update game status
      setInCheck(data.inCheck || false);
      setPossibleMoves(data.legalMoves || {});

      if (data.gameOver) {
        setGameStatus({
          isGameOver: true,
          result: data.result
        });
      }
    };

    // Listen for game updates
    socket.on('game:update', (data) => {
      if (!data) return;

      console.log("Game update received:", data.board, data.position);
      setBoard(data.board || initialBoard());
      setPosition(data.position || null);
      setCurrentTurn(data.currentTurn || 'white');
      setInCheck(data.inCheck || false);
      setPossibleMoves(data.legalMoves || {});

      if (data.lastMove) {
        setMoves(prevMoves => [...prevMoves, data.lastMove]);
      } else if (data.history) {
        setMoves(data.history); // fallback khi undo hoặc khi join game
      }

      if (data.gameOver) {
        setGameStatus({
          isGameOver: true,
          result: data.result
        });
      }

      // If this update is from an undo operation, update our stacks
      if (data.isUndoOperation && moves.length > 0) {
        // Get the last move from our history
        const lastMove = moves[moves.length - 1];

        // Update our stacks
        setMoveHistory(prevHistory => prevHistory.slice(0, -1));
        setUndoStack(prevStack => [...prevStack, lastMove]);
      }

      // If this update is from a redo operation, update our stacks
      if (data.isRedoOperation && undoStack.length > 0) {
        // Get the move we just redid
        const redoneMove = undoStack[undoStack.length - 1];

        // Update our stacks
        setUndoStack(prevStack => prevStack.slice(0, -1));
        setMoveHistory(prevHistory => [...prevHistory, redoneMove]);
      }
    });

    // Listen for game data when joining
    socket.on('game:data', handleGameData);

    // Listen for move validation failures
    socket.on('game:moveRejected', (error) => {
      console.error('Move rejected:', error);
      // Reset any UI state needed after failed move
    });

    // Cleanup on unmount
    return () => {
      socket.off('game:update');
      socket.off('game:data');
      socket.off('game:moveRejected');
      socket.off('game:undoDeclined');
      socket.off('game:undoConfirmed');
      socket.emit('game:leave', { gameId });
    };
  }, [gameId, user]);

  // Handle undo declined event
  useEffect(() => {
    const handleUndoDeclined = (data) => {
      if (!data) return;
      alert(`${data.by || 'Opponent'} has declined the undo request.`);
    };

    socket.on('game:undoDeclined', handleUndoDeclined);

    return () => {
      socket.off('game:undoDeclined', handleUndoDeclined);
    };
  }, []);

  // Handle undo confirmed event
  useEffect(() => {
    const handleUndoConfirmed = (data) => {
      if (!data || !user) return;

      const username = user.username || 'You';

      // If we're the requester, ignore this event
      if (data.by === username) return;

      const accept = window.confirm(`${data.by || 'Opponent'} wants to undo the last move. Do you agree?`);
      socket.emit('game:undoResponse', {
        gameId,
        accepted: accept
      });
    };

    socket.on('game:undoConfirmed', handleUndoConfirmed);

    return () => {
      socket.off('game:undoConfirmed', handleUndoConfirmed);
    };
  }, [gameId, user]);

  // Add event listener for undo request response
  useEffect(() => {
    socket.on('game:undoResponse', (data) => {
      if (data.accepted) {
        // If opponent accepts, perform the actual undo
        socket.emit('game:undo', { gameId });
      } else {
        alert('Opponent declined your undo request');
      }
    });

    return () => {
      socket.off('game:undoResponse');
    };
  }, [gameId]);

  // Add event listeners for undo/redo events with the new names
  useEffect(() => {
    // Listen for undo requests from opponent
    socket.on('confirm_undo_request', (data) => {
      const requesterUsername = data.requesterUsername || 'Opponent';
      const accept = window.confirm(`${requesterUsername} wants to undo the last move. Do you agree?`);

      if (accept) {
        socket.emit('accept_undo', { gameId });
      } else {
        socket.emit('reject_undo', { gameId });
      }
    });

    // Listen for undo responses
    socket.on('undo_accepted', () => {
      console.log('Undo request was accepted');
      // No need to do anything - server will broadcast the updated state
    });

    socket.on('undo_rejected', (data) => {
      alert(`${data.by || 'Opponent'} declined your undo request`);
    });

    return () => {
      socket.off('confirm_undo_request');
      socket.off('undo_accepted');
      socket.off('undo_rejected');
    };
  }, [gameId, moves, undoStack]); // Include moves and undoStack in the dependency array

  // Handle piece selection and move highlighting
  const selectPiece = useCallback((piece, position) => {
    // Only allow selection of the player's own pieces during their turn
    if (piece && piece.color === playerColor && currentTurn === playerColor) {
      setSelectedPiece({ piece, position });
      // Highlight possible moves for this piece
    } else {
      setSelectedPiece(null);
    }
  }, [playerColor, currentTurn]);

  // Make a move
  const makeMove = useCallback((from, to) => {
    if (gameStatus.isGameOver || currentTurn !== playerColor) {
      return false;
    }

    // Send move to server
    socket.emit('game:move', {
      gameId,
      from,
      to
    });

    return true;
  }, [gameId, currentTurn, playerColor, gameStatus.isGameOver]);

  // Update the undo function to use the new event name
  const handleUndoRequest = useCallback(() => {
    if (!gameId || gameStatus.isGameOver || currentTurn !== playerColor) {
      return false;
    }

    // Request an undo using the new event name
    socket.emit('request_undo', { gameId });
    console.log('Undo requested for game:', gameId);

    return true;
  }, [gameId, currentTurn, playerColor, gameStatus.isGameOver]);

  // Update the redo function to use the new event name
  const handleRedoRequest = useCallback(() => {
    if (!gameId || gameStatus.isGameOver || !undoStack || undoStack.length === 0) {
      return false;
    }

    // Request a redo using the new event name
    socket.emit('request_redo', { gameId });
    console.log('Redo requested for game:', gameId);

    return true;
  }, [gameId, gameStatus.isGameOver, undoStack]);

  // Reset game
  const reset = useCallback(() => {
    socket.emit('game:reset', { gameId });
  }, [gameId]);

  return {
    board,
    position,
    currentTurn,
    playerColor,
    moves,
    inCheck,
    possibleMoves,
    selectedPiece,
    gameStatus,
    moveHistory,
    undoStack: undoStack, // Explicitly return for clarity
    selectPiece,
    makeMove,
    undo: handleUndoRequest,
    redo: handleRedoRequest,
    reset,
    // Return futurePositions as undoStack for backward compatibility
    futurePositions: undoStack 
  };
};

// Helper function to create initial empty board
function initialBoard() {
  // Create 8x8 empty board representation
  return Array(8).fill().map(() => Array(8).fill(null));
}

export default useChess;
