import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import Board from "../components/Board";
import MoveHistory from "../components/MoveHistory";
import GameControls from "../components/GameControls";
import Spinner from "../components/Spinner";
import { socket } from "../services/socket";
import useChess from "../hooks/useChess";
import "../styles/Play.css";

const Play = () => {
  const { gameId } = useParams();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth();
  const [opponent, setOpponent] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [gameOver, setGameOver] = useState(false);
  const [gameResult, setGameResult] = useState(null);
  const [showResignDialog, setShowResignDialog] = useState(false);
  const [showDrawOfferDialog, setShowDrawOfferDialog] = useState(false);
  const [receivedDrawOffer, setReceivedDrawOffer] = useState(false);
  
  // Debug state
  const [showDebug, setShowDebug] = useState(process.env.NODE_ENV === 'development');
  const [debugInfo, setDebugInfo] = useState({
    gameId: gameId || 'Not set',
    socketConnected: socket.connected,
    loadAttempts: 0,
    errors: []
  });

  // Get chess game state from custom hook - MUST BE CALLED UNCONDITIONALLY
  const {
    board,
    currentTurn,
    playerColor,
    moves,
    makeMove,
    position,
    inCheck,
    possibleMoves,
    gameStatus
  } = useChess(gameId);
  
  // Add game:matched event handler
  useEffect(() => {
    const handleMatchFound = (data) => {
      console.log("Match found!", data ? JSON.stringify(data) : 'No data');
      
      try {
        if (!data) {
          throw new Error("Empty match data received");
        }
        
        if (!data.gameId) {
          throw new Error("Invalid match data - missing gameId");
        }
        
        // Make sure gameId is a string and not "undefined"
        if (typeof data.gameId !== 'string' || data.gameId === "undefined") {
          throw new Error(`Invalid gameId format: ${data.gameId}`);
        }
        
        console.log(`✓ Valid gameId received: ${data.gameId}`);
        navigate(`/play/${data.gameId}`);
      } catch (error) {
        console.error("Error processing match data:", error.message, data);
        setDebugInfo(prev => ({
          ...prev,
          errors: [...prev.errors, {
            time: new Date().toLocaleTimeString(),
            message: `Match error: ${error.message}`
          }]
        }));
      }
    };
    
    socket.on("game:matched", handleMatchFound);
    
    return () => {
      socket.off("game:matched", handleMatchFound);
    };
  }, [navigate]);
  
  // Debug: Check for undefined gameId in URL
  useEffect(() => {
    if (window.location.pathname.includes('/play/undefined')) {
      console.error("⚠️ DETECTED 'undefined' IN URL PATH! This is the bug we're looking for.");
      setDebugInfo(prev => ({
        ...prev,
        errors: [...prev.errors, {
          time: new Date().toLocaleTimeString(),
          message: "URL contains 'undefined' instead of a valid gameId"
        }]
      }));
      
      // Redirect to play page after short delay
      setTimeout(() => navigate('/play'), 3000);
    } else if (gameId) {
      console.log(`🎮 Game ID from URL params: ${gameId}`);
      setDebugInfo(prev => ({
        ...prev,
        gameId: gameId
      }));
    }
  }, [gameId, navigate]);
  
  // Handle authentication redirect
  useEffect(() => {
    if (!isAuthenticated && !isLoading) {
      navigate("/signin");
    }
  }, [isAuthenticated, navigate, isLoading]);

  // Debug: Track socket connection
  useEffect(() => {
    const handleConnect = () => {
      console.log("🔌 Socket connected");
      setDebugInfo(prev => ({
        ...prev,
        socketConnected: true
      }));
    };
    
    const handleDisconnect = () => {
      console.log("🔌 Socket disconnected");
      setDebugInfo(prev => ({
        ...prev,
        socketConnected: false,
        errors: [...prev.errors, {
          time: new Date().toLocaleTimeString(),
          message: "Socket disconnected while on game page"
        }]
      }));
    };
    
    socket.on('connect', handleConnect);
    socket.on('disconnect', handleDisconnect);
    
    return () => {
      socket.off('connect', handleConnect);
      socket.off('disconnect', handleDisconnect);
    };
  }, []);

  // Initial game setup
  useEffect(() => {
    if (!gameId || !isAuthenticated) {
      if (!gameId) {
        console.log("No gameId provided, showing matchmaking");
      }
      return;
    }

    setIsLoading(true);
    
    // Debug: Log load attempt
    setDebugInfo(prev => ({
      ...prev,
      loadAttempts: prev.loadAttempts + 1
    }));
    
    console.log(`🎲 Joining game room for gameId: ${gameId}`);

    // Join game room
    socket.emit("game:join", { gameId });

    // Get game data
    const handleGameData = (data) => {
      try {
        console.log(`📊 Received game data for game ${gameId}:`, data ? JSON.stringify(data) : 'No data');
        
        // SAFETY CHECK: Ensure data is valid before processing
        if (!data) {
          throw new Error("Received empty game data");
        }
        
        // Get opponent information - add safety check here
        let opponentPlayer = null;
        if (data.players && Array.isArray(data.players)) {
          opponentPlayer = data.players.find(player => {
            return player && typeof player === 'object' && player.id && user && user.id && player.id !== user.id;
          });
        }
        
        if (opponentPlayer) {
          setOpponent(opponentPlayer);
          console.log(`👤 Found opponent: ${opponentPlayer.username || 'Unknown'}`);
        } else {
          console.warn("⚠️ Could not find opponent in game data");
          setDebugInfo(prev => ({
            ...prev,
            errors: [...prev.errors, {
              time: new Date().toLocaleTimeString(),
              message: "Could not find opponent in game data"
            }]
          }));
        }
      } catch (error) {
        console.error("Error processing game data:", error.message);
        setDebugInfo(prev => ({
          ...prev,
          errors: [...prev.errors, {
            time: new Date().toLocaleTimeString(),
            message: `Game data error: ${error.message}`
          }]
        }));
      } finally {
        setIsLoading(false);
      }
    };
    
    const handleGameError = (error) => {
      try {
        console.error(`❌ Game error:`, error);
        const errorMessage = error && typeof error === 'object' ? 
          (error.message || JSON.stringify(error)) : 
          String(error);
        
        setDebugInfo(prev => ({
          ...prev,
          errors: [...prev.errors, {
            time: new Date().toLocaleTimeString(),
            message: `Game error: ${errorMessage}`
          }]
        }));
      } catch (e) {
        console.error("Error handling game error:", e);
      } finally {
        setIsLoading(false);
      }
    };
    
    socket.on("game:data", handleGameData);
    socket.on("game:error", handleGameError);

    // Listen for game over events
    socket.on("game:over", (data) => {
      try {
        console.log(`🏁 Game over:`, data);
        setGameOver(true);
        setGameResult(data && data.result ? data.result : null);
      } catch (error) {
        console.error("Error handling game over:", error);
      }
    });

    // Listen for draw offers
    socket.on("game:drawOffered", (data) => {
      try {
        if (data && data.from) {
          console.log(`🤝 Draw offered by ${data.from}`);
        } else {
          console.log(`🤝 Draw offered`);
        }
        setReceivedDrawOffer(true);
      } catch (error) {
        console.error("Error handling draw offer:", error);
      }
    });

    return () => {
      // Clean up socket listeners when component unmounts
      try {
        console.log(`🧹 Cleaning up socket listeners and leaving game ${gameId}`);
        socket.off("game:data", handleGameData);
        socket.off("game:error", handleGameError);
        socket.off("game:over");
        socket.off("game:drawOffered");
        socket.emit("game:leave", { gameId });
      } catch (error) {
        console.error("Error during cleanup:", error);
      }
    };
  }, [gameId, isAuthenticated, user]);

  // Check for game status changes
  useEffect(() => {
    if (gameStatus && gameStatus.isGameOver) {
      try {
        console.log(`♟️ Game status change to game over:`, gameStatus);
        setGameOver(true);
        setGameResult(gameStatus.result || null);
      } catch (error) {
        console.error("Error handling game status change:", error);
      }
    }
  }, [gameStatus]);
  
  const handleResign = () => {
    try {
      console.log(`🏳️ Resigning game ${gameId}`);
      socket.emit("game:resign", { gameId });
      setGameOver(true);
      setGameResult({ 
        type: "resignation", 
        winner: opponent && opponent.id ? opponent.id : null
      });
      setShowResignDialog(false);
    } catch (error) {
      console.error("Error handling resign:", error);
    }
  };

  const handleOfferDraw = () => {
    try {
      console.log(`🤝 Offering draw in game ${gameId}`);
      socket.emit("game:offerDraw", { gameId });
      setShowDrawOfferDialog(false);
    } catch (error) {
      console.error("Error offering draw:", error);
    }
  };

  const handleAcceptDraw = () => {
    try {
      console.log(`🤝 Accepting draw in game ${gameId}`);
      socket.emit("game:acceptDraw", { gameId });
      setGameOver(true);
      setGameResult({ type: "draw", reason: "agreement" });
      setReceivedDrawOffer(false);
    } catch (error) {
      console.error("Error accepting draw:", error);
    }
  };

  const handleDeclineDraw = () => {
    try {
      console.log(`👎 Declining draw in game ${gameId}`);
      socket.emit("game:declineDraw", { gameId });
      setReceivedDrawOffer(false);
    } catch (error) {
      console.error("Error declining draw:", error);
    }
  };

  const setUndo = () => {
    try {
      console.log(`⏪ Requesting undo in game ${gameId}`);
      socket.emit("game:requestUndo", { gameId });
    } catch (error) {
      console.error("Error requesting undo:", error);
    }
  };

  const setRedo = () => {
    try {
      console.log(`⏩ Requesting redo in game ${gameId}`);
      socket.emit("game:requestRedo", { gameId });
    } catch (error) {
      console.error("Error requesting redo:", error); 
    }
  };

  // NOW we can use conditional rendering after all hooks have been called
  
  // Safeguard - if user is not available, don't proceed with rendering
  if (!user && !isLoading) {
    console.error("User object is undefined but authentication check is complete");
    return (
      <div className="error-container">
        <h2>Authentication Error</h2>
        <p>Unable to retrieve your user information. Please try logging in again.</p>
        <button onClick={() => navigate('/signin')}>Go to Sign In</button>
      </div>
    );
  }
  
  // If no gameId is present, we need to show matchmaking UI instead
  if (!gameId) {
    return (
      <div className="matchmaking-container">
        <h2>Find a Match</h2>
        
        <div className="matchmaking-options">
          <button onClick={() => {
            try {
              console.log("Looking for casual match");
              socket.emit("game:findMatch", { gameMode: "casual", timeControl: "10min" });
            } catch (error) {
              console.error("Error finding casual match:", error);
            }
          }}>
            Find Casual Match
          </button>
          
          <button onClick={() => {
            try {
              console.log("Looking for ranked match");
              socket.emit("game:findMatch", { gameMode: "ranked", timeControl: "10min" });
            } catch (error) {
              console.error("Error finding ranked match:", error);
            }
          }}>
            Find Ranked Match
          </button>
        </div>
        
        {/* Debug listener for game:matched */}
        {showDebug && (
          <div className="debug-box" style={{
            marginTop: '20px',
            padding: '10px',
            border: '1px solid #666',
            backgroundColor: '#222',
            color: '#eee',
            fontSize: '12px'
          }}>
            <h4>Debug: Listening for match</h4>
            <p>Socket connected: <span style={{ color: socket.connected ? '#4CAF50' : '#F44336' }}>
              {socket.connected ? 'Yes' : 'No'}
            </span></p>
          </div>
        )}
      </div>
    );
  }

  if (isLoading) {
    return (
      <>
        <Spinner />
        {showDebug && (
          <div style={{ position: 'fixed', bottom: '10px', right: '10px', background: '#222', color: '#fff', padding: '10px', maxWidth: '300px', fontSize: '11px' }}>
            <p>Loading game: {gameId}</p>
            <p>Attempt #{debugInfo.loadAttempts}</p>
            <p>Socket connected: {debugInfo.socketConnected ? 'Yes' : 'No'}</p>
          </div>
        )}
      </>
    );
  }

  // Safeguard against rendering with missing user data
  if (!user) {
    return (
      <div className="error-container" style={{ padding: "20px", textAlign: "center" }}>
        <h2>User Data Not Available</h2>
        <p>Unable to load user data. Please try refreshing or sign in again.</p>
        <button 
          onClick={() => navigate('/signin')}
          style={{ padding: "10px 20px", marginTop: "20px" }}
        >
          Go to Sign In
        </button>
      </div>
    );
  }

  return (
    <div className="play-container">
      {/* Debug panel */}
      {showDebug && (
        <div style={{
          position: 'fixed',
          top: '10px',
          right: '10px',
          backgroundColor: 'rgba(0,0,0,0.8)',
          color: 'white',
          padding: '10px',
          zIndex: 1000,
          fontSize: '11px',
          maxWidth: '300px',
          maxHeight: '300px',
          overflow: 'auto'
        }}>
          <button 
            onClick={() => setShowDebug(false)} 
            style={{float: 'right', marginLeft: '5px'}}
          >
            X
          </button>
          <h4>Game Debug</h4>
          <p>Game ID: <code>{debugInfo.gameId || 'Not set'}</code></p>
          <p>Socket: <span style={{color: debugInfo.socketConnected ? 'lime' : 'red'}}>
            {debugInfo.socketConnected ? 'Connected' : 'Disconnected'}
          </span></p>
          <p>Player: {user ? (typeof user.username === 'string' ? user.username : 'Invalid username') : 'Unknown'}</p>
          <p>Opponent: {opponent ? (typeof opponent.username === 'string' ? opponent.username : 'Invalid username') : 'Unknown'}</p>
          <p>Color: {typeof playerColor === 'string' ? playerColor : 'Unknown'}</p>
          <p>Turn: {typeof currentTurn === 'string' ? currentTurn : 'Unknown'}</p>
          
          {debugInfo.errors && debugInfo.errors.length > 0 && (
            <div>
              <h5>Errors:</h5>
              <ul style={{paddingLeft: '15px', margin: '5px 0'}}>
                {debugInfo.errors.map((err, i) => (
                  <li key={i}>[{err.time}] {typeof err.message === 'string' ? err.message : 'Error object'}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      <div className="game-info">
        {opponent && (
          <div className="opponent-info">
            <h3>{typeof opponent.username === 'string' ? opponent.username : 'Opponent'}</h3>
            <div className="rating">Rating: {opponent.elo || '?'}</div>
          </div>
        )}
        
        <div className="game-status">
          {gameOver ? (
            <div className="game-result">
              <h2>Game Over</h2>
              {gameResult && gameResult.type === "checkmate" && (
                <p>
                  Checkmate! {gameResult.winner === (user && user.id) 
                    ? "You won" 
                    : "You lost"}
                </p>
              )}
              {gameResult && gameResult.type === "draw" && (
                <p>Game drawn by {gameResult.reason || 'agreement'}</p>
              )}
              {gameResult && gameResult.type === "resignation" && (
                <p>
                  {gameResult.winner === (user && user.id)
                    ? "Opponent resigned. You won!"
                    : "You resigned. You lost."}
                </p>
              )}
              {gameResult && gameResult.type === "timeout" && (
                <p>
                  {gameResult.winner === (user && user.id)
                    ? "Opponent ran out of time. You won!"
                    : "You ran out of time. You lost."}
                </p>
              )}
              <button 
                className="new-game-btn"
                onClick={() => navigate("/lobby")}
              >
                Back to Lobby
              </button>
            </div>
          ) : (
            <div>
              <div className="current-turn">
                {currentTurn === playerColor ? "Your turn" : "Opponent's turn"}
              </div>
              {inCheck && <div className="check-alert">Check!</div>}
            </div>
          )}
        </div>
      </div>

      <div className="game-board-container">
        <Board
          board={Array.isArray(board) ? board : []}
          position={position && typeof position === 'object' ? position : {}}
          playerColor={typeof playerColor === 'string' ? playerColor : 'white'}
          possibleMoves={possibleMoves && typeof possibleMoves === 'object' ? possibleMoves : {}}
          onMove={makeMove}
          disabled={gameOver || currentTurn !== playerColor}
        />
      </div>

      <div className="game-sidebar">
        <div className="move-history-container">
          <MoveHistory moves={Array.isArray(moves) ? moves : []} />
        </div>
        
        <div className="game-controls-container">
          <GameControls
            onNewGame={() => navigate("/lobby")}
            canResign={!gameOver}
            onResign={() => setShowResignDialog(true)}
            canOfferDraw={!gameOver}
            onOfferDraw={() => setShowDrawOfferDialog(true)}
            canUndo={(Array.isArray(moves) && moves.length > 0) && !gameOver}
            onUndoRequest={() => setUndo()}
            canRedo={true}
            onRedoRequest={() => setRedo()}
          />
        </div>

        {/* Player info */}
        <div className="player-info">
          <h3>You ({typeof playerColor === 'string' ? playerColor : 'unknown'})</h3>
          <div className="rating">Rating: {user && typeof user.elo !== 'undefined' ? user.elo : '?'}</div>
        </div>

        {/* Debug button */}
        {process.env.NODE_ENV === 'development' && !showDebug && (
          <button 
            onClick={() => setShowDebug(true)}
            style={{
              position: 'fixed',
              bottom: '10px',
              right: '10px',
              background: '#444',
              color: '#fff',
              border: 'none',
              padding: '5px 10px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '12px'
            }}
          >
            Show Debug
          </button>
        )}
      </div>

      {/* Resign confirmation dialog */}
      {showResignDialog && (
        <div className="dialog-overlay">
          <div className="dialog">
            <h3>Resign Game?</h3>
            <p>Are you sure you want to resign? This will count as a loss.</p>
            <div className="dialog-buttons">
              <button onClick={handleResign} className="confirm-btn">
                Yes, Resign
              </button>
              <button onClick={() => setShowResignDialog(false)} className="cancel-btn">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Draw offer dialog */}
      {showDrawOfferDialog && (
        <div className="dialog-overlay">
          <div className="dialog">
            <h3>Offer Draw?</h3>
            <p>Send a draw offer to your opponent?</p>
            <div className="dialog-buttons">
              <button onClick={handleOfferDraw} className="confirm-btn">
                Yes, Offer Draw
              </button>
              <button onClick={() => setShowDrawOfferDialog(false)} className="cancel-btn">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Received draw offer dialog */}
      {receivedDrawOffer && (
        <div className="dialog-overlay">
          <div className="dialog">
            <h3>Draw Offered</h3>
            <p>Your opponent has offered a draw. Do you accept?</p>
            <div className="dialog-buttons">
              <button onClick={handleAcceptDraw} className="confirm-btn">
                Accept Draw
              </button>
              <button onClick={handleDeclineDraw} className="cancel-btn">
                Decline
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Play;
